import { useState } from "react";
import Board from "./ticTacToe/Board";
import checkBoard from "../lib/ticTacToe";

export default function TicTacToe() {
  const [history, setHistory] = useState([Array(9).fill(0)]);
  const [winningSquares, setWinners] = useState([]);

  const currentSquares = history[history.length - 1];
  const playersTurn = history.length % 2 ? "X" : "O";
  const isDraw = history.length > 9;

  const statusMessage = winningSquares[0]
    ? `Player - ${winningSquares[0] > 0 ? "X" : "O"} - Wins!`
    : isDraw
      ? "GAME OVER - It's a draw"
      : `Player - ${playersTurn}'s Turn`;

  function handlePlay(i) {
    if (winningSquares[0] || currentSquares[i]) return;
    const nextSquares = [...currentSquares];
    nextSquares[i] = history.length % 2 ? 1 : -1;
    const winner = checkBoard(nextSquares);
    if (winner) setWinners(winner);
    setHistory([...history, nextSquares]);
  }

  return (
    <>
      <h1>{statusMessage}</h1>
      <Board
        squares={currentSquares}
        onPlay={handlePlay}
        winningSquares={winningSquares}
        isComplete={winningSquares[0] || isDraw}
        playersTurn={playersTurn}
      />
    </>
  );
}
