import { useState } from "react";
import Square from "./Square";

export default function Board({ squares, onPlay, complete }) {
  return (
    <div className={complete ? "complete" : ""}>
      {[0, 1, 2].map((row) => (
        <div className="board-row" key={`row-${row}`}>
          {[0, 1, 2].map((square) => {
            const squareIdx = row * 3 + square;
            return (
              <Square
                key={`square-${squareIdx}`}
                value={squares[squareIdx]}
                onSquareClick={() => onPlay(squareIdx)}
              />
            );
          })}
        </div>
      ))}
    </div>
  );
}
