import { useState, useEffect } from "react";
import Board from "./ticTacToe/Board";
import checkBoard from "../lib/ticTacToe";

export default function TicTacToe() {
  const [history, setHistory] = useState([Array(9).fill(0)]);
  const [turnsTaken, updateTurns] = useState(0);
  const currentSquares = history[history.length - 1];

  useEffect(() => {
    if (turnsTaken > 4) alert("checking now.");
    if (turnsTaken === 9) alert("game over.");
  }, [turnsTaken]);

  function handleSquareClick(i) {
    console.log("click");
    if (squares[i]) return;
    const nextSquares = [...squares];
    nextSquares[i] = turnsTaken % 2 ? -1 : 1;
    setSquares(nextSquares);
    updateTurns(turnsTaken + 1);
  }

  return (
    <>
      <Board squares={currentSquares} onSquareClick={handleSquareClick} />
    </>
  );
}
