import { useState } from "react";
import Square from "./Square";

export default function Board() {
  const [squares, setSquares] = useState(Array(9).fill(0));
  const [turnsTaken, updateTurns] = useState(0);

  function handleClick(i) {
    const nextSquares = [...squares];
    nextSquares[i] = turnsTaken % 2 ? -1 : 1;
    updateTurns(turnsTaken + 1);
    setSquares(nextSquares);
  }

  return (
    <>
      {[0, 1, 2].map((row) => (
        <div className="board-row" key={`row-${row}`}>
          {[0, 1, 2].map((square) => {
            const squareIdx = row * 3 + square;
            return (
              <Square
                key={`square-${squareIdx}`}
                value={squares[squareIdx]}
                onSquareClick={() => handleClick(squareIdx)}
              />
            );
          })}
        </div>
      ))}
    </>
  );
}
